import React, { useEffect, useState } from "react";

function App() {
  const [url, setUrl] = useState("");
  const [disableScroll, setDisableScroll] = useState(false);
  const [sandboxValue, setSandboxValue] = useState("");

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    let dest = params.get("destination");
    const cors = params.get("cors") === "true";
    const stay = params.get("stay");
    const redirectStop = params.get("redirectstop") === "true";
    const blockAds = params.get("ads") === "true";

    if (dest) {
      if (!/^https?:\/\//i.test(dest)) {
        dest = "https://" + dest;
      }

      if (cors) {
        dest = `https://corsstuff.vercel.app/api/proy?url=${encodeURIComponent(dest)}`;
      }

      if (
        stay === "true" &&
        (dest.includes("easyfun.gg") || dest.startsWith("https://roblx-ten.vercel.app"))
      ) {
        setDisableScroll(true);
      }

      if (redirectStop) {
        setSandboxValue("allow-scripts allow-presentation allow-same-origin");
      }

      if (blockAds) {
        injectAdBlockCSS();
      }

      setUrl(dest);
    }
  }, []);

  // Function to inject the adblock CSS
  const injectAdBlockCSS = () => {
    const style = document.createElement("style");
    style.innerHTML = `
      A:link[HREF*="javascript:void"]  IMG,
      A:link[HREF*="addata"]  IMG,
      A:link[HREF*="ad."]  IMG,
      A:link[HREF*="ads."]  IMG,
      A:link[HREF*="/ad"]  IMG,
      A:link[HREF*="/A="]  IMG,
      A:link[HREF*="/click"]  IMG,
      A:link[HREF*="?click"]  IMG,
      A:link[HREF*="?banner"]  IMG,
      A:link[HREF*="=click"]  IMG,
      A:link[HREF*="clickurl="]  IMG,
      A:link[HREF*=".atwola."]  IMG,
      A:link[HREF*="spinbox."]  IMG,
      A:link[HREF*="transfer.go"]  IMG,
      A:link[HREF*="adfarm"]  IMG,
      A:link[HREF*="adSpace"]  IMG,
      A:link[HREF*="adserve"]  IMG,
      A:link[HREF*=".banner"]  IMG,
      A:link[HREF*="bluestreak"]  IMG,
      A:link[HREF*="doubleclick"]  IMG,
      A:link[HREF*="/rd."]  IMG,
      A:link[HREF*="/0AD"]  IMG,
      A:link[HREF*=".falkag."]  IMG,
      A:link[HREF*="trackoffer."]  IMG,
      A:link[HREF*="casalemedia."]  IMG,
      A:link[HREF*="valueclick."]  IMG,
      A:link[HREF*="betterbasketball."]  IMG,
      A:link[HREF*="sponsors.phtml"]  IMG,
      A:link[HREF*="realgmtix.phtml"]  IMG,
      A:link[HREF*="BurstingPipe"]  IMG,
      A:link[HREF*="ebayobjects"]  IMG,
      A:link[HREF*="tracksponsor."]  IMG { display: none !important }

      IFRAME[SRC*="addata"],
      IFRAME[SRC*="ad."],
      IFRAME[SRC*="ads."],
      IFRAME[SRC*="/ad"],
      IFRAME[SRC*="/A="],
      IFRAME[SRC*="/click"],
      IFRAME[SRC*="?click"],
      IFRAME[SRC*="?banner"],
      IFRAME[SRC*="=click"],
      IFRAME[SRC*="clickurl="],
      IFRAME[SRC*=".atwola."],
      IFRAME[SRC*="spinbox."],
      IFRAME[SRC*="transfer.go"],
      IFRAME[SRC*="adfarm"],
      IFRAME[SRC*="adSpace"],
      IFRAME[SRC*="adserve"],
      IFRAME[SRC*="adjuggler"],
      IFRAME[SRC*=".banner"],
      IFRAME[SRC*="bluestreak"],
      IFRAME[SRC*="doubleclick"],
      IFRAME[SRC*="/rd."],
      IFRAME[SRC*="/0AD"],
      IFRAME[SRC*=".falkag."],
      IFRAME[SRC*="trackoffer."],
      IFRAME[SRC*="connextra."],
      IFRAME[ID*="merchandisingMERC"],
      IFRAME[SRC*="tracksponsor."] { display: none !important }

      A:link[onmouseover*="AdSolution"] IMG,
      *[class=sponsors],
      *[class=sp_links],
      *[class=advertising],
      *[ID=sponsors],
      *[ID=ad],
      *[ID=inlinead],
      *[ID=ad_creative],
      *[ID=contextualLinks],
      IMG[SRC*=".msads."] { display: none !important }

      DIV[id*="pagelet_adbox"],
      DIV[id*="pagelet_pymkbox"],
      DIV[id*="pagelet_connectbox"] { display: none !important }
    `;
    document.head.appendChild(style);
  };

  if (!url) {
    return (
      <div
        style={{
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          fontFamily: "sans-serif",
          color: "#555",
        }}
      >
        <div>
          <h2>No destination set</h2>
          <p>
            Add{" "}
            <code style={{ backgroundColor: "#eee", padding: "2px 4px" }}>
              ?destination=example.com
            </code>{" "}
            to the URL.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        overflow: "hidden",
        position: "relative",
      }}
    >
      <iframe
        src={url}
        title="Proy Frame"
        style={{
          border: "none",
          width: "98vw",
          height: "98vh",
        }}
        scrolling={disableScroll ? "no" : "yes"}
        sandbox={sandboxValue || undefined}
      />
    </div>
  );
}

export default App;
