import React, { useEffect, useState } from "react";

function App() {
  const [pageContent, setPageContent] = useState("");
  const [error, setError] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    let dest = params.get("destination");
    const cors = params.get("cors") === "true";
    const redirectStop = params.get("redirectstop") === "true";

    if (!dest) {
      setError("No destination set");
      return;
    }

    if (!/^https?:\/\//i.test(dest)) {
      dest = "https://" + dest;
    }

    const proyUrl = cors
      ? `https://corsstuff.vercel.app/api/proy?url=${encodeURIComponent(dest)}`
      : dest;

    fetch(proyUrl)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error ${res.status}`);
        }
        return res.text();
      })
      .then((html) => {
        if (redirectStop) {
          const parser = new DOMParser();
          const doc = parser.parseFromString(html, "text/html");

          // Remove scripts
          doc.querySelectorAll("script").forEach((el) => el.remove());

          // Remove meta refresh redirects
          doc
            .querySelectorAll('meta[http-equiv="refresh"]')
            .forEach((el) => el.remove());

          // Remove link rel="prerender"
          doc
            .querySelectorAll('link[rel="prerender"]')
            .forEach((el) => el.remove());

          // You can add more sandboxing rules here if needed

          setPageContent(doc.documentElement.innerHTML);
        } else {
          setPageContent(html);
        }
      })
      .catch((err) => {
        console.error(err);
        setError(err.message);
      });
  }, []);

  if (error) {
    return (
      <div
        style={{
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          fontFamily: "sans-serif",
          color: "#555",
        }}
      >
        <div>
          <h2>Error</h2>
          <p>{error}</p>
          <p>
            Try adding{" "}
            <code style={{ backgroundColor: "#eee", padding: "2px 4px" }}>
              ?destination=example.com
            </code>{" "}
            to the URL.
          </p>
        </div>
      </div>
    );
  }

  if (!pageContent) {
    return (
      <div
        style={{
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          fontFamily: "sans-serif",
          color: "#555",
        }}
      >
        <div>
          <h2>Loading content...</h2>
        </div>
      </div>
    );
  }

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        overflow: "auto",
        backgroundColor: "#fff",
      }}
      dangerouslySetInnerHTML={{ __html: pageContent }}
    />
  );
}

export default App;
